package com.example.root.moviekedua.helper;public class MoviesModel {    private String mov_title;    private String mov_overview;    private String mov_release;    private String mov_poster;    private String mov_backdrop;    private String mov_rate;    private String mov_rate_average;    public MoviesModel(){    }    public MoviesModel(String title, String overview, String release, String poster, String backdrop, String rate, String rate_avg){        this.mov_title      = title;        this.mov_overview   = overview;        this.mov_release    = release;        this.mov_poster     = poster;        this.mov_backdrop   = backdrop;        this.mov_rate       = rate;        this.mov_rate_average = rate_avg;    }    public String getMov_title() {        return mov_title;    }    public void setMov_title(String mov_title) {        this.mov_title = mov_title;    }    public String getMov_overview() {        return mov_overview;    }    public void setMov_overview(String mov_overview) {        this.mov_overview = mov_overview;    }    public String getMov_release() {        return mov_release;    }    public void setMov_release(String mov_release) {        this.mov_release = mov_release;    }    public String getMov_poster() {        return mov_poster;    }    public void setMov_poster(String mov_poster) {        this.mov_poster = mov_poster;    }    public String getMov_backdrop() {        return mov_backdrop;    }    public void setMov_backdrop(String mov_backdrop) {        this.mov_backdrop = mov_backdrop;    }    public String getMov_rate() {        return mov_rate;    }    public void setMov_rate(String mov_rate) {        this.mov_rate = mov_rate;    }    public String getMov_rate_average() {        return mov_rate_average;    }    public void setMov_rate_average(String mov_rate_average) {        this.mov_rate_average = mov_rate_average;    }}